export const timestampContractSource = `
  pragma solidity ^0.8.0;

  contract TimestampContract {
    function getTimestamp(uint size) public view returns (uint[] memory) {
      uint[] memory arr = new uint[](size);
      for(uint i = 0; i < size; i++) {
          arr[i] = block.timestamp;
      }
      return arr;
    }
  }
`

